import React, { Component } from 'react';

import CoolCard from './Home/coolcard.js';
import {
  withRouter
} from 'react-router-dom'

import Header from './Home/Header.js';
import dummyData from './../services/DummyData';
import UserList from './UserView/UserList';
import { InputLabel, Input } from '@material-ui/core';
import { Select } from '@material-ui/core';
import { MenuItem} from '@material-ui/core';
import TextField from '@material-ui/core/TextField';
import InviteUserButton from './Common/InviteUserButton.js'

import './../styles/Users.css';
import {Container, Row, Col } from 'react-bootstrap'

class TrackingLocations extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
        selected: "All",
        trackingLocation: ""
    }

    this.handleLogout = this.handleLogout.bind(this);
    this.handleChangeTL = this.handleChangeTL.bind(this);
  }

  handleLogout() {
    this.props.history.push('/');
  }
  handleChange(value) {
    this.setState({selected: value });
  }

  handleChangeTL(value) {
    this.setState({trackingLocation: value})
    console.log(this.state.trackingLocation)
  };
  render() {
      return (

        <div>
          <Header handleLogout={() => this.handleLogout} pageName="Tracking Locations"/>
          <div className="Users">


        {/* <Container fixed style = {{ alignItems: 'center', display: 'grid', 'grid-template-columns': '250px 250px 250px', 'justify-content':'center', 'grid-gap': '80px'}}> */}

        <div className="flex-grid">
        <div className="col">
        <Input
          type = "text"
          placeholder="Search"
          onChange={ event => this.handleChangeTL(event.target.value) } 
          />
        </div>

        <div className="col">
        </div>

        <div className ="col">
            ///////////////////////////Charlie put your thing here/////////////////////////////////
            <InviteUserButton />
          </div>

        </div>
        {/* </Container> */}


          <div className='tc'>

          <h1>Tacking Location List</h1>
          <UserList users={dummyData.users.filter(user => {
    return (user.role === this.state.selected || this.state.selected == "All") 
    && (user.name.toLowerCase().includes(this.state.username.toLowerCase()) || this.state.username === "");
})} prop= {this.props}/> 
          </div>

          <br></br>

          </div>
        </div>

      );
  }
}

export default withRouter(TrackingLocations);
