import React, { Component } from 'react';
import './../../styles/Login.css';
import ResetPasswordButton from './../Common/ResetPasswordButton.js';
import TextField from '@material-ui/core/TextField';
import BasicButton from './../Common/BasicButton.js';




import ACTIONS from '../../redux/action'
import { connect } from 'react-redux';

class Login extends Component {




  render() {

    const handleLogin = () => {
    if (email == '') {
      alert('Please enter a valid email address.');
    } else {
      UserServices.sendResetPasswordLink(email);
      alert('Sent link to ' + email + ' to reset your password' + '.');
      setOpen(false);
    }
  }

    return (
      <div className="Login">
        <header className="Login-header">
          <p>
            Welcome to Excel Tracker!
          </p>
        </header>
        <div className="Login-body">
          <form>
            Username: <TextField id="standard-basic" label="Username" /> <br/>
            Password: <TextField id="standard-basic" label="Password" /> <br/>

            <BasicButton label = "Login" onclick={handleLogin}/>

          </form>
            <ResetPasswordButton/>

        </div>

      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  currentUser: state.user.currentUser
});
const mapDispatchToProps = dispatch => {
  return {
    setCurrentUser : (currentUser) => dispatch(ACTIONS.setCurrentUser(currentUser))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
